INSERT INTO {DB_PREFIX}templates ( TEMPLATE_ID, TEMPLATE_NAME, TEMPLATE_CONTENT, STYLESHEET, ENCODING, ACTIVE, DEFAULT_TEMPLATE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 19, 'Minimal template', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\r\n<!-- Change lang=\"en\" to the language of your site -->\r\n\r\n<head>\r\n\r\n<title>{sitename} - {title}</title>\r\n<!-- The sitename is changed in Site Admin/Global settings. {title} is the name of each page -->\r\n\r\n{metadata}\r\n<!-- Don\'t remove this! Metadata is entered in Site Admin/Global settings. -->\r\n\r\n{stylesheet}\r\n<!-- This is how all the stylesheets attached to this template are linked to -->\r\n\r\n</head>\r\n\r\n<body>\r\n\r\n      <!-- Start Navigation -->\r\n      <div style=\"float: left; width: 25%;\">\r\n         {menu template=\'minimal_menu.tpl\'}\r\n      </div>\r\n      <!-- End Navigation -->\r\n\r\n      <!-- Start Content -->\r\n      <div>\r\n         <h2>{title}</h2>\r\n         {content} \r\n      </div>\r\n      <!-- End Content -->\r\n\r\n</body>\r\n</html>', null, null, 1, 0, '2006-07-25 21:22:33', '2006-07-25 21:22:33' );
