INSERT INTO {DB_PREFIX}module_templates ( MODULE_NAME, TEMPLATE_NAME, CONTENT, CREATE_DATE, MODIFIED_DATE ) VALUES ( 'News', 'displaysummary', '<!-- Start News Display Template -->\r\n{foreach from=$items item=entry}\r\n\r\n<div class=\"NewsSummaryLink\">\r\n	{$entry->titlelink}\r\n</div>\r\n\r\n<div class=\"NewsSummaryCategory\">\r\n	({$entry->category})\r\n</div\r\n\r\n{if $entry->formatpostdate}\r\n	<div class=\"NewsSummaryPostdate\">\r\n		{$entry->formatpostdate}\r\n	</div\r\n{/if}\r\n\r\n{if $entry->summary}\r\n	<div class=\"NewsSummarySummary\">\r\n		{eval var=$entry->summary}\r\n	</div>\r\n\r\n	<div class=\"NewsSummaryMorelink\">\r\n		[{$entry->morelink}]\r\n	</div>\r\n\r\n{else if $entry->content}\r\n\r\n	<div class=\"NewsSummaryContent\">\r\n		{eval var=$entry->content}\r\n	</div>\r\n{/if}\r\n\r\n\r\n{/foreach}\r\n<!-- End News Display Template -->', '2005-11-29 09:26:29', '2005-11-29 09:26:29' );
INSERT INTO {DB_PREFIX}module_templates ( MODULE_NAME, TEMPLATE_NAME, CONTENT, CREATE_DATE, MODIFIED_DATE ) VALUES ( 'News', 'displaydetail', '<h3 id=\"NewsPostDetailTitle\">{$entry->title}</h3>\n\n<hr id=\"NewsPostDetailHorizRule\">\n\n{if $entry->category}\n\n<div id=\"NewsPostDetailCategory\">\n{$entry->category}\n</div>\n\n{/if}\n\n{if $entry->postdate}\n\n<div id=\"NewsPostDetailDate\">\nPosted: {$entry->postdate|date_format}\n</div>\n\n{/if}\n\n{if $entry->summary}\n\n<div id=\"NewsPostDetailSummary\">\n<strong>\n{eval var=$entry->summary}\n</strong>\n</div>\n\n{/if}\n\n<div id=\"NewsPostDetailContent\">\n{eval var=$entry->content}\n</div>\n\n<div>\n{$entry->printlink}\n</div>', '2005-11-29 09:26:29', '2005-11-29 09:26:29' );
